mapper.select:

	SELECT *
	  FROM WFNEWS.PUBLISHED_INCIDENT_DETAIL
	 WHERE (PUBLISHED_INCIDENT_DETAIL_GUID = #{publishedIncidentDetailGuid}
  	  OR INCIDENT_NUMBER_LABEL = #{publishedIncidentDetailGuid}
			OR INCIDENT_NAME = #{publishedIncidentDetailGuid})

		 AND fire_year = #{fireYear, javaType=java.lang.Integer, jdbcType=NUMERIC, mode=IN}

	 LIMIT 1

SELECT PUBLISHED_INCIDENT_DETAIL_GUID, INCIDENT_GUID, INCIDENT_NUMBER_LABEL, NEWS_CREATED_TIMESTAMP, STAGE_OF_CONTROL_CODE, FIRE_CENTRE_CODE, FIRE_CENTRE_NAME, FIRE_YEAR, GENERAL_INCIDENT_CAUSE_CAT_ID, NEWS_PUBLICATION_STATUS_CODE, DISCOVERY_DATE, DECLARED_OUT_DATE, FIRE_OF_NOTE_IND, INCIDENT_NAME, INCIDENT_LOCATION, TRADITIONAL_TERRITORY_DETAIL, INCIDENT_SIZE_ESTIMATED_HA, INCIDENT_SIZE_MAPPED_HA, INCIDENT_SIZE_DETAIL, INCIDENT_CAUSE_DETAIL, WILDFIRE_CREW_RESOURCES_IND, WILDFIRE_AVIATION_RESOURCE_IND, HEAVY_EQUIPMENT_RESOURCES_IND, INCIDENT_MGMNT_CREW_RSRC_IND, STRUCTURE_PROTECTION_RSRC_IND, PUBLISHED_TIMESTAMP, LAST_UPDATED_TIMESTAMP, CREATE_DATE, UPDATE_DATE, LATITUDE, LONGITUDE, RESPONSE_TYPE_CODE, RESPONSE_TYPE_DETAIL FROM WFNEWS.PUBLISHED_INCIDENT_DETAIL WHERE stage_of_control_code IS NOT NULL
AND (stage_of_control_code = 'OUT' AND fire_year = #{currentFireYear, javaType=java.lang.Integer, jdbcType=NUMERIC, mode=IN}
OR stage_of_control_code IN
#{soc}
)
AND stage_of_control_code IN
#{soc}
AND geometry && ST_MakeEnvelope (#{xmin, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{ymin, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{xmax, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{ymax, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, )
AND ST_DWithin(ST_Transform(geometry, ), ST_Transform(ST_SetSRID(ST_MakePoint(#{longitude, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{latitude, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}), ), ), #{radius, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN})
AND DISCOVERY_DATE BETWEEN NOW() - INTERVAL '24 HOURS' AND NOW()
AND fire_of_note_ind = 'Y'
AND fire_of_note_ind = 'N'
AND fire_centre_code = #{fireCentreCode, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}
AND fire_centre_name = #{fireCentreName, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}
AND CREATE_DATE >= #{fromCreateDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}
AND CREATE_DATE <= #{toCreateDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}
AND DISCOVERY_DATE >= #{fromDiscoveryDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}
AND DISCOVERY_DATE <= #{toDiscoveryDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}
AND UPPER(CONCAT(incident_location, '', CONCAT(fire_centre_code, '', CONCAT(incident_number_label, '', incident_name)))) LIKE '%'||UPPER(#{searchText})||'%'
ORDER BY
incident_number_label fire_of_note_ind incident_location incident_name fire_centre_code fire_centre_name stage_of_control_code last_updated_timestamp discovery_date create_date ASC, ASC DESC, DESC
OFFSET #{offset, javaType=java.lang.Integer, jdbcType=INTEGER, mode=IN} ROWS FETCH NEXT #{pageRowCount, javaType=java.lang.Integer, jdbcType=INTEGER, mode=IN} ROWS ONLY

SELECT COUNT(*)
FROM WFNEWS.PUBLISHED_INCIDENT_DETAIL
WHERE stage_of_control_code IS NOT NULL

      AND (stage_of_control_code = 'OUT' AND fire_year = #{currentFireYear, javaType=java.lang.Integer, jdbcType=NUMERIC, mode=IN}

				OR stage_of_control_code IN


					#{soc}


		)


			AND stage_of_control_code IN

			#{soc}



	AND geometry && ST_MakeEnvelope (#{xmin, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{ymin, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{xmax, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{ymax, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, )


	AND ST_DWithin(ST_Transform(geometry, ), ST_Transform(ST_SetSRID(ST_MakePoint(#{longitude, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{latitude, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}), ), ), #{radius, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN})


	AND DISCOVERY_DATE BETWEEN NOW() - INTERVAL '24 HOURS' AND NOW()


	AND fire_of_note_ind = 'Y'


	AND fire_of_note_ind = 'N'


	AND fire_centre_code = #{fireCentreCode, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}


	AND fire_centre_name = #{fireCentreName, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}


	AND CREATE_DATE >= #{fromCreateDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}


	AND CREATE_DATE <= #{toCreateDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}


	AND DISCOVERY_DATE >= #{fromDiscoveryDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}


	AND DISCOVERY_DATE <= #{toDiscoveryDate, javaType=java.util.Date, jdbcType=TIMESTAMP, mode=IN}


	AND UPPER(CONCAT(incident_location, '', CONCAT(fire_centre_code, '', CONCAT(incident_number_label, '', incident_name))))
	LIKE '%'||UPPER(#{searchText})||'%'

              SELECT jsonb_build_object('type', 'FeatureCollection', 'features', jsonb_agg(features.feature)) AS fc
  		FROM (SELECT JSONB_BUILD_OBJECT(
            'type', 'Feature',
            'geometry',   ST_AsGeoJSON(t.geometry, options => 1)::JSONB,
            'properties', TO_JSONB(t.*) - 'geometry'
           ) AS feature
		          FROM (SELECT f.incident_number_label, f.incident_name, f.fire_year, f.geometry FROM wfnews.published_incident_detail AS f
		         WHERE stage_of_control_code = #{stageOfControlCode, javaType=java.lang.String, jdbcType=VARCHAR, mode=IN}

		           AND fire_year = #{currentFireYear, javaType=java.lang.Integer, jdbcType=NUMERIC, mode=IN}


		           AND fire_of_note_ind = 'N'

		           AND geometry && ST_MakeEnvelope (#{xmin, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{ymin, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{xmax, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, #{ymax, javaType=java.lang.Double, jdbcType=DOUBLE, mode=IN}, )) t
		  ) features
